const p = msg.payload || {};
const out1 = { payload: Number(p.ldr), topic: p.device || 'esp32' };
const out2 = { payload: Number(p.ldr), topic: (p.device || 'esp32') + ':ldr' };
return [out1, out2];

// Deterministic rule-based control
const TH_LOW  = 1200; // LED ON below this
const TH_HIGH = 1800; // LED OFF above this
const p = msg.payload || {};
const ldr = Number(p.ldr);
const device = p.device || 'esp32-01';

let ledState = flow.get('ledState');
if (ledState !== 0 && ledState !== 1) ledState = 0;

let changed = false;
let reason = 'No change';

if (ldr < TH_LOW && ledState === 0) {
  ledState = 1;
  reason = `Rule: LDR (${ldr}) < TH_LOW (${TH_LOW}) → LED ON`;
  changed = true;
} else if (ldr > TH_HIGH && ledState === 1) {
  ledState = 0;
  reason = `Rule: LDR (${ldr}) > TH_HIGH (${TH_HIGH}) → LED OFF`;
  changed = true;
}

flow.set('ledState', ledState);
if (!changed) return null; // publish only on change

msg.topic = 'cps/esp32/actuator';
msg.payload = { device, led: ledState, reason };
return msg;

const p = msg.payload || {};
const v = Number(p.ldr);
const device = p.device || 'esp32-01';
const ledState = flow.get('ledState') || 0;

// rolling window
const N = 20;
let arr = flow.get('ldr_window') || [];
if (!Number.isNaN(v)) { arr.push(v); if (arr.length > N) arr.shift(); }
flow.set('ldr_window', arr);

const min = Math.min(...arr);
const max = Math.max(...arr);
const avg = arr.reduce((a,b)=>a+b,0)/(arr.length||1);
let trend='stable';
if (arr.length>=6) { const d=arr[arr.length-1]-arr[0]; if (d>80) trend='increasing'; else if (d<-80) trend='decreasing'; }

msg.topic='gpt4o';
msg.payload=[
 'You are a CPS monitoring assistant.',
 'Explain the situation in 1-2 short sentences for students.',
 'Do NOT give control instructions.',
 'Return ONLY the explanation text.',
 '',
 `Device: ${device}`,
 `Latest LDR: ${v}`,
 `Avg: ${avg.toFixed(1)}, Min: ${min}, Max: ${max}, Trend: ${trend}`,
 `LED (rule-based): ${ledState?'ON':'OFF'}`
].join('\n');
msg.temperature=0.3; msg.max_tokens=80; return msg;
